			return 1;
		}else if (this.rankc >= 0) {
			this.rankc--;
			return 2;
		}else if (this.rankd >= 0) {
			this.rankd--;
			return 3;
		}else if (this.ranke >= 0) {
			this.ranke--;
			return 4;
		}else{
			return 5;
		}
	}
}

var monsters = new Array();

for (var i = $('li').length - 1; i >= 0; i--) {
	var nome = $($($('li')[i]).children('a')[0]).html();
	var origem = $($($('li')[i]).children('a')[1]).html();
	$($('li')[i]).html($($('li')[i]).html().replace(/<a.*a>/, ''));
	$($('li')[i]).html($($('li')[i]).html().replace(") - ", ''));
	$($('li')[i]).html($($('li')[i]).html().replace(")", ''));
	$($('li')[i]).html($($('li')[i]).html().replace("(", ''));
	$($('li')[i]).html($($('li')[i]).html().replace("Japanese", ''));
	monsters.push({
		id: i,
		name: nome,
		origin: origem,
		description: $($('li')[i]).html()
	});
}
function shuffleArray(array) {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
    return array;
}
monsters = shuffleArray(monsters);

for (var i = monsters.length - 1; i >= 0; i--) {
	monsters[i].rank = ranks.defineRank();
}


console.log(monsters.length);
$('body').html(JSON.stringify(monsters));

